const baseImageInput = document.getElementById("base-image");
const watermarkImageInput = document.getElementById("watermark-image-input");
const watermarkTextInput = document.getElementById("watermark-text-input");
const fontSelect = document.getElementById("font-select");
const textColorInput = document.querySelectorAll('input[name^="text-color"]');
const downloadBtn = document.getElementById("download-btn");
const cancelBtn = document.getElementById("cancel-btn");
const canvasContainer = document.getElementById("canvas-container");
const canvasElement = document.getElementById("canvas");
const watermarkImage = document.getElementById("watermark-image");
const watermarkText = document.getElementById("watermark-text");

const fabricCanvas = new fabric.Canvas("canvas", {
  preserveObjectStacking: true,
});

fabricCanvas.setWidth(360);
fabricCanvas.setHeight(400);

const imgElement = new Image();
imgElement.crossOrigin = "anonymous";
imgElement.src =
  "data:image/png;base64,";
imgElement.onload = function () {
  const img = new fabric.Image(imgElement, {
    selectable: false,
    evented: false,
    left: fabricCanvas.width / 2,
    top: fabricCanvas.height / 2,
    originX: "center",
    originY: "center",
  });

  img.scaleToWidth(fabricCanvas.width);
  img.scaleToHeight(fabricCanvas.height);

  // Set the image as the background image of the canvas
  fabricCanvas.setBackgroundImage(
    img,
    fabricCanvas.renderAll.bind(fabricCanvas)
  );
  saveState();
};

async function loadImage(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = (e) => {
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.onload = () => resolve(img);
      img.onerror = reject;
      img.src = e.target.result;
    };
    reader.onerror = reject;
    reader.readAsDataURL(file);
  });
}

async function addWatermarkImage(file) {
  try {
    const img = await loadImage(file);
    const fabricImg = new fabric.Image(img, {
      left: fabricCanvas.width / 2,
      top: fabricCanvas.height / 2,
      originX: "center",
      originY: "center",
      scaleX: 0.3,
      scaleY: 0.3,
      hasControls: true,
      hasBorders: true,
    });
    fabricCanvas.add(fabricImg).setActiveObject(fabricImg);
    saveState();
  } catch (error) {
    alert("Failed to load watermark image.");
    console.error(error);
  }
}

function addWatermarkText(text, font, color) {
  if (!text) return;
  const fabricText = new fabric.Textbox(text, {
    left: fabricCanvas.width / 2,
    top: fabricCanvas.height / 2,
    originX: "center",
    originY: "center",
    fontSize: 40,
    fontFamily: font,
    fill: color,
    editable: true,
    hasControls: true,
    hasBorders: true,
  });
  fabricCanvas.add(fabricText).setActiveObject(fabricText);
  saveState();
}

