watermarkImageInput.addEventListener("change", async (e) => {
  const file = e.target.files[0];
  if (file && file.type.startsWith("image/")) {
    await addWatermarkImage(file);
  } else {
    alert("Please upload a valid image file.");
  }
});

watermarkTextInput.addEventListener("change", (e) => {
  const text = e.target.value.trim();
  const activeObject = fabricCanvas.getActiveObject();
  if (activeObject && activeObject.type === "textbox") {
    activeObject.set("text", text);
    fabricCanvas.renderAll();
    saveState();
  } else if (text) {
    const colorVal = textColorInput.value ? textColorInput.value : "#000000";
    addWatermarkText(text, fontSelect.value, colorVal);
  }
});

fontSelect.addEventListener("change", (e) => {
  const font = e.target.value;
  const activeObject = fabricCanvas.getActiveObject();
  if (activeObject && activeObject.type === "textbox") {
    activeObject.set("fontFamily", font);
    fabricCanvas.renderAll();
    saveState();
  }
});

textColorInput.forEach((input) => {
  input.addEventListener("change", (e) => {
    if (e.target.checked) {
      let textColor = e.target.value;
      textColorInput.forEach((otherInput) => {
        if (otherInput !== e.target) {
          otherInput.checked = false;
        }
      });
      const activeObject = fabricCanvas.getActiveObject();
      if (activeObject && activeObject.type === "textbox") {
        activeObject.set("fill", textColor);
        fabricCanvas.renderAll();
        saveState();
      }
    }
  });

  // Ensure the span is clickable to toggle the checkbox
  input.nextElementSibling.addEventListener("click", () => {
    input.checked = true;
    input.dispatchEvent(new Event("change"));
  });
});

downloadBtn.addEventListener("click", () => {
  fabricCanvas.discardActiveObject();
  fabricCanvas.renderAll();

  // Ensure all images have the crossOrigin attribute set
  fabricCanvas.getObjects("image").forEach((img) => {
    if (img.getElement().crossOrigin !== "anonymous") {
      img.getElement().crossOrigin = "anonymous";
    }
  });

  // Attempt to export the canvas to a data URL
  try {
    const dataURL = fabricCanvas.toDataURL({
      format: "png",
      quality: 1,
    });
    const link = document.createElement("a");
    link.href = dataURL;
    link.download = "watermarked-image.png";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  } catch (error) {
    console.error("Failed to export canvas: ", error);
    alert(
      "Failed to export canvas. Ensure all images are from the same origin or have appropriate CORS headers."
    );
  }
});

cancelBtn.addEventListener("click", () => {
  fabricCanvas.clear();
  fabricCanvas.loadFromJSON(
    history[0],
    fabricCanvas.renderAll.bind(fabricCanvas)
  );
});

let history = [];
let historyStep = -1;

function saveState() {
  history = history.slice(0, historyStep + 1);
  history.push(JSON.stringify(fabricCanvas));
  historyStep++;
}

function undo() {
  if (historyStep > 0) {
    historyStep--;
    fabricCanvas.clear();
    fabricCanvas.loadFromJSON(
      history[historyStep],
      fabricCanvas.renderAll.bind(fabricCanvas)
    );
  }
}

function redo() {
  if (historyStep < history.length - 1) {
    historyStep++;
    fabricCanvas.clear();
    fabricCanvas.loadFromJSON(
      history[historyStep],
      fabricCanvas.renderAll.bind(fabricCanvas)
    );
  }
}

fabricCanvas.on("object:added", saveState);
fabricCanvas.on("object:modified", saveState);
fabricCanvas.on("object:removed", saveState);

document.addEventListener("keydown", (e) => {
  if (e.key === "z" && (e.ctrlKey || e.metaKey)) {
    e.preventDefault();
    undo();
  } else if (e.key === "y" && (e.ctrlKey || e.metaKey)) {
    e.preventDefault();
    redo();
  }
});

let isPanning = false;
let startPan = { x: 0, y: 0 };

canvasContainer.addEventListener("wheel", (e) => {
  e.preventDefault();
  const delta = e.deltaY;
  let zoom = fabricCanvas.getZoom();
  xoom *= 0.999 ** delta;
  zoom = Math.min(Math.max(zoom, 0.5), 3);
  fabricCanvas.zoomToPoint({ x: e.offsetX, y: e.offsetY }, zoom);
});

canvasContainer.addEventListener("mousedown", (e) => {
  if (e.button === 1) {
    isPanning = true;
    startPan = { x: e.clientX, y: e.clientY };
    canvasContainer.style.cursor = "grabbing";
  }
});

document.addEventListener("mousemove", (e) => {
  if (isPanning) {
    const delta = {
      x: e.clientX - startPan.x,
      y: e.clientY - startPan.y,
    };
    fabricCanvas.relativePan(delta);
    startPan = { x: e.clientX, y: e.clientY };
  }
});

document.addEventListener("mouseup", () => {
  if (isPanning) {
    isPanning = false;
    canvasContainer.style.cursor = "default";
  }
});

interact(".watermark-element")
  .draggable({
    listeners: {
      move(event) {
        const target = event.target;
        const x = (parseFloat(target.getAttribute("data-x")) || 0) + event.dx;
        const y = (parseFloat(target.getAttribute("data-y")) || 0) + event.dy;

        target.style.transform = `translate(${x}px, ${y}px)`;
        target.setAttribute("data-x", x);
        target.setAttribute("data-y", y);
      },
      end(event) {
        const target = event.target;
        const rect = target.getBoundingClientRect();
        const canvasRect = canvasContainer.getBoundingClientRect();

        let left = rect.left - canvasRect.left;
        let top = rect.top - canvasRect.top;

        left = Math.max(
          0,
          Math.min(left, fabricCanvas.width - target.offsetWidth)
        );
        top = Math.max(
          0,
          Math.min(top, fabricCanvas.height - target.offsetHeight)
        );

        target.style.left = `${left}px`;
        target.style.top = `${top}px`;
        target.style.transform = "translate(0px, 0px)";
        target.removeAttribute("data-x");
        target.removeAttribute("data-y");

        saveState();
      },
    },
  })
  .resizable({
    edges: { left: true, right: true, bottom: true, top: true },
    listeners: {
      move(event) {
        const target = event.target;
        let x = parseFloat(target.getAttribute("data-x")) || 0;
        let y = parseFloat(target.getAttribute("data-y")) || 0;

        target.style.width = `${event.rect.width}px`;
        target.style.height = `${event.rect.height}px`;

        x += event.deltaRect.left;
        y += event.deltaRect.top;

        target.style.transform = `translate(${x}px, ${y}px)`;
        target.setAttribute("data-x", x);
        target.setAttribute("data-y", y);
      },
      end(event) {
        const target = event.target;
        const rect = target.getBoundingClientRect();
        const canvasRect = canvasContainer.getBoundingClientRect();

        const left =
          rect.left -
          canvasRect.left +
          (parseFloat(target.getAttribute("data-x")) || 0);
        const top =
          rect.top -
          canvasRect.top +
          (parseFloat(target.getAttribute("data-y")) || 0);

        left = Math.max(
          0,
          Math.min(left, fabricCanvas.width - target.offsetWidth)
        );
        top = Math.max(
          0,
          Math.min(top, fabricCanvas.height - target.offsetHeight)
        );

        target.style.left = `${left}px`;
        target.style.top = `${top}px`;
        target.style.transform = "translate(0px, 0px)";
        target.removeAttribute("data-x");
        target.removeAttribute("data-y");

        saveState();
      },
    },
    modifiers: [
      interact.modifiers.restrictSize({
        min: { width: 50, height: 50 },
      }),
    ],
    inertia: true,
  });

const undoBtn = document.createElement("button");
undoBtn.textContent = "Undo";
document.querySelector(".container").appendChild(undoBtn);

const redoBtn = document.createAttribute("button");
redoBtn.textContent = "Redo";
document.querySelector(".container").appendChild(redoBtn);

undoBtn.addEventListener("click", undo);
redoBtn.addEventListener("click", redo);

saveState();

document.addEventListener("keydown", (e) => {
  const activeObject = fabricCanvas.getActiveObject();
  if (activeObject) {
    switch (e.key) {
      case "ArrowLeft":
        activeObject.left -= 5;
        fabricCanvas.renderAll();
        saveState();
        break;
      case "ArrowRight":
        activeObject.left += 5;
        fabricCanvas.renderAll();
        saveState();
        break;
      case "ArrowUp":
        activeObject.top -= 5;
        fabricCanvas.renderAll();
        saveState();
        break;
      case "ArrowDown":
        activeObject.top += 5;
        fabricCanvas.renderAll();
        saveState();
        break;
      case "+":
        activeObject.scaleX *= 1.1;
        activeObject.scaleY *= 1.1;
        fabricCanvas.renderAll();
        saveState();
        break;
      case "-":
        activeObject.scaleX *= 0.9;
        activeObject.scaleY *= 0.9;
        fabricCanvas.renderAll();
        saveState();
        break;
    }
  }
});
